import Vue from 'vue';
import { httpAction } from '@/api/manage';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import * as qiniu from 'qiniu-js';
export default {
  name: "ProductAddAlbumModal",
  data: function data() {
    return {
      title: '新增专辑',
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      headers: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 17
        }
      },
      model: {},
      coverimg: '',
      canimg: '',
      percent: '',
      loading: false,
      url: {
        add: '/user/album/add',
        fileUpload: window._CONFIG['domianQiNiu'],
        qiniuToken: '/qiniuUpToken'
      }
    };
  },
  created: function created() {
    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    };
  },
  methods: {
    add: function add() {
      this.form.resetFields();
      this.confirmLoading = false;
    },
    handleOK: function handleOK() {
      var _this = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var formData = Object.assign(_this.model, values);
          formData.albumimg = _this.coverimg; //时间格式化

          console.log(formData);
          httpAction(_this.url.add, formData, 'POST').then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.handleCancel();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
      this.visible = false;
    },
    // 自定义上传
    customRequest: function customRequest(data) {
      var _this2 = this;

      var suffix = "." + data.file.name.replace(/^.+\./, ''); //请求token

      httpAction(this.url.qiniuToken, {}, 'post').then(function (res) {
        if (res) {
          var token = res.data.token;
          var file = data.file;
          var key = new Date().getTime() + suffix;
          var domain = _this2.url.fileUpload;
          var config = {
            useCdnDomain: true,
            region: null
          };
          var putExtra = {
            fname: '',
            params: {},
            mimeType: null
          };
          var observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: function next(res) {
              _this2.loading = true;
              _this2.percent = res.total.percent.toFixed(2);
              _this2.coverimg = '';
            },
            error: function error() {
              _this2.$message.error('上传失败');

              _this2.loading = false;
              _this2.coverimg = '';
            },
            complete: function complete(response) {
              _this2.coverimg = "".concat(domain, "/").concat(response.key);
              _this2.loading = false;
            }
          });
        }
      });
    }
  }
};
