import { postAction } from "@/api/manage";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import JEllipsis from "@/components/jeecg/JEllipsis";
export default {
  name: "ServiceDeliveryList",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis: JEllipsis
  },
  data: function data() {
    return {
      description: '服务商邮寄列表页面',
      // 表头
      columns: [{
        title: '用户昵称',
        align: "center",
        dataIndex: 'nickName'
      }, {
        title: '手机号',
        align: "center",
        dataIndex: 'phone'
      }, {
        title: '订单编号',
        align: "center",
        dataIndex: 'code'
      }, {
        title: '创建时间',
        align: "center",
        dataIndex: 'createTime'
      }, {
        title: '支付时间',
        align: "center",
        dataIndex: 'payTime'
      }, {
        title: '收件人',
        align: "center",
        dataIndex: 'name'
      }, {
        title: '收件人电话',
        align: "center",
        dataIndex: 'userPhone'
      }, {
        title: '详细地址',
        align: "center",
        dataIndex: 'address',
        scopedSlots: {
          customRender: 'templateAddress'
        }
      }],
      url: {
        list: "/system/ambassadororder/pageList"
      }
    };
  },
  methods: {
    loadData: function loadData(arg) {
      var _this = this;

      if (!this.url.list) {
        this.$message.error("请设置url.list属性!");
        return;
      } //加载数据 若传入参数1则加载第一页的内容


      if (arg === 1) {
        this.ipagination.current = 1;
      }

      var params = this.getQueryParams(); //查询条件

      this.loading = true;
      postAction(this.url.list, params).then(function (res) {
        if (res.success) {
          _this.dataSource = res.result.records;
          _this.ipagination.total = res.result.total;
        }

        if (res.code === 510) {
          _this.$message.warning(res.message);
        }

        _this.loading = false;
      });
    },
    getQueryParams: function getQueryParams() {
      //获取查询条件
      var sqp = {};

      if (this.superQueryParams) {
        sqp['superQueryParams'] = encodeURI(this.superQueryParams);
      }

      var param = Object.assign(sqp, this.queryParam, this.isorter, this.filters);
      param.field = this.getQueryField();
      param.pageNo = this.ipagination.current;
      param.pageSize = this.ipagination.pageSize;
      param.phone = param.phone ? param.phone : '';
      var formData = "field=".concat(param.field, "&pageNo=").concat(param.pageNo, "&pageSize=").concat(param.pageSize, "&phone=").concat(param.phone);
      return formData;
    },
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    //  修改角色
    handelRole: function handelRole(record) {
      this.$refs.roleModel.open(record);
    }
  }
};
