import Vue from 'vue';
import { ACCESS_TOKEN } from "@/store/mutation-types";
export default {
  name: "PdfPreviewModal",
  data: function data() {
    return {
      url: window._CONFIG['pdfDomainURL'],
      id: "pdfPreviewIframe",
      headers: {}
    };
  },
  created: function created() {
    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    };
  },
  computed: {},
  mounted: function mounted() {
    window.addEventListener('message', this.handleScanFileMessage);
  },
  methods: {
    handleScanFileMessage: function handleScanFileMessage(event) {
      // 根据上面制定的结构来解析iframe内部发回来的数据
      var data = event.data;
      console.log(data);
    },
    previewFiles: function previewFiles(title, token) {
      var iframe = document.getElementById("pdfPreviewIframe");
      var json = {
        "title": title,
        "token": token
      };
      iframe.contentWindow.postMessage(json, "*");
    }
  }
};
