import ProductVipNewAddModal from "./modules/ProductVipNewAddModal";
import ProductNewBigImg from "./modules/ProductNewBigImg";
import ProductNewAuditModel from "./modules/ProductNewAuditModel";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import { getAction } from "@/api/manage";
import JEllipsis from "@/components/jeecg/JEllipsis";
export default {
  name: "ProductVipNewList",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis: JEllipsis,
    ProductNewBigImg: ProductNewBigImg,
    ProductNewAuditModel: ProductNewAuditModel,
    ProductVipNewAddModal: ProductVipNewAddModal
  },
  data: function data() {
    return {
      description: 'VIP课程管理管理页面',
      // 表头
      columns: [{
        title: '课程标题',
        align: "center",
        dataIndex: 'title'
      }, {
        title: '课程简介',
        align: "center",
        dataIndex: 'introduction',
        scopedSlots: {
          customRender: 'templateContent'
        }
      }, {
        title: '课程作者',
        align: "center",
        dataIndex: 'author'
      }, {
        title: '课程分类',
        align: "center",
        dataIndex: 'category'
      }, {
        title: '课程封面',
        align: "center",
        dataIndex: 'coverimg',
        scopedSlots: {
          customRender: 'avatar'
        }
      }, {
        title: '浏览次数',
        align: "center",
        dataIndex: 'clickcount'
      }, {
        title: '发布日期',
        align: "center",
        dataIndex: 'createTime'
      }, {
        title: '操作',
        dataIndex: 'action',
        align: "center",
        scopedSlots: {
          customRender: 'action'
        }
      }],
      selectList: [],
      url: {
        list: "/product/productNew/vipList",
        delete: "/product/productNew/delete",
        selectNewList: "/product/productCategory/sysCategory",
        auditTrue: '/product/productNew/auditTrue',
        //审核通过
        commen: '/product/productNew/recommended' // 推荐设置

      }
    };
  },
  created: function created() {
    var _this = this;

    getAction(this.url.selectNewList).then(function (res) {
      if (res.success) {
        _this.selectList = res.result;
      } else {
        _this.$message.warning(res.message);
      }
    });
  },
  methods: {
    clickThenCheck: function clickThenCheck(record) {
      var _this2 = this;

      return {
        on: {
          click: function click() {
            _this2.$refs.modalForm.edit(record);

            _this2.$refs.modalForm.title = "详情";
            _this2.$refs.modalForm.disableSubmit = true;
          }
        }
      };
    },
    //  图片点击放大
    bigImg: function bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    //  审核通过，请求接口
    handleAuditTrue: function handleAuditTrue(id) {
      if (!this.url.auditTrue) {
        this.$message.error("请设置审核属性!");
        return;
      }

      var that = this;
      getAction(this.url.auditTrue, {
        id: id
      }).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    //  审核拒绝，弹出框=>拒绝理由
    handleShenJu: function handleShenJu(id) {
      this.$refs.auditModel.open(id);
    },
    //  推荐设置
    handleCommen: function handleCommen(id, level, set) {
      var arr = {
        id: id,
        level: level,
        set: set
      };
      var that = this;
      getAction(this.url.commen, arr).then(function (res) {
        if (res.success) {
          that.$message.success(res.message);
        } else {
          that.$message.warning(res.message);
        }

        that.loadData();
      });
    }
  }
};
