import { filterObj, formatDate } from "@/utils/util";
import ShareBonusModal from "./modules/ShareBonusModal";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import moment from 'moment'
export default {
  name: "ServiceProvidersList",
  mixins: [JeecgListMixin],
  components: {
    ShareBonusModal,
    ProductNewBigImg
  },
  data() {
    return {
      queryParam: {
        yearMonth: "",
        month:"",
        code: "",
        phone: "",
        qualification: "",
        role: ""
      },
      param: {
        yearMonth: "",
        month:"",
        code: "",
        phone: "",
        qualification: "",
        role: ""
      },
      total: "",
      description: "服务商列表页面",
      type: "ambassadorList",
      // 表头
      columns: [
        {
          title: "手机号",
          align: "center",
          dataIndex: "phone"
        },
        {
          title: "V云号",
          align: "center",
          dataIndex: "code"
        },
        {
          title: "昵称",
          align: "center",
          dataIndex: "nickName"
        },
        {
          title: "头像",
          align: "center",
          dataIndex: "header",
          scopedSlots: { customRender: "header" }
        },
        {
          title: "角色",
          align: "center",
          dataIndex: "userRoleName"
          // dataIndex: 'bigVip',
          // customRender: function (text, record) {
          //   if (text === 1) {
          //     return '大V会员'
          //   } else {
          //     if (record.isVip === 0) {
          //       return '普通会员'
          //     } else if (record.isVip === 1) {
          //       return '7天vip'
          //     } else if (record.isVip === 2) {
          //       return '月度vip'
          //     } else if (record.isVip === 3) {
          //       return '季度vip'
          //     } else if (record.isVip === 4) {
          //       return '年费vip'
          //     }
          //   }
          // }
        },
        {
          title: "V值",
          align: "center",
          dataIndex: "credits"
        }
      ],
      
      url: {
        list: "/system/appuserinfo/ambassadorList"
      }
    };
  },
  created() {
    const nowTime = new Date().getTime();
    const month = formatDate(nowTime, 'yyyy-MM');
    this.queryParam.yearMonth = moment(month, "yyyy-MM");
    this.queryParam.month = month;
  },
  methods: {
    getQueryParams() {
      var param = Object.assign({}, this.queryParam, this.isorter);
      param.field = this.getQueryField();
      param.pageNo = this.ipagination.current;
      param.pageSize = this.ipagination.pageSize;
      return filterObj(param);
    },
    //  图片点击放大
    bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    // 分红
    shareBonus() {
      this.$refs.modalForm.open();
    },
    searchReset() {
      this.queryParam.code = "";
      this.queryParam.phone = "";
      this.queryParam.role = "";
      this.queryParam.qualification = "";
      this.queryParam.month = "";
      this.queryParam.yearMonth = "";
       this.queryParam.total = "";
      this.loadData(1);
    },
    onDateChange: function(value,dateString) {
    this.queryParam.month = dateString;
    },
    onDateOk(value) {
      // console.log(value)
    }
  }
};
