import { getAction } from "@/api/manage";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import { filterObj } from "@/utils/util";

export default {
  name: "AppUserMoneyDetailList",
  mixins: [JeecgListMixin],
  components: {},
  data() {
    return {
      description: "消费流水记录管理页面",
      typeList: [],
      type: this.$route.query.type,
      // 表头
      columns: [
        {
          title: "用户昵称",
          align: "center",
          dataIndex: "nickName"
        },
        {
          title: "类型",
          align: "center",
          dataIndex: "type"
        },
        {
          title: "支出/收益",
          align: "center",
          dataIndex: "detailType"
        },
        {
          title: "金额",
          align: "center",
          dataIndex: "money"
        },
        {
          title: "创建时间",
          align: "center",
          dataIndex: "createTime",
          scopedSlots: { customRender: "createTime" }
        },
        {
          title: "支付方式",
          align: "center",
          dataIndex: "payType",
          customRender: function(text, record) {
            if (record.info === "充值会员" || record.info === "充值V币") {
              if (text === 1) {
                return "支付宝";
              } else if (text === 2) {
                return "微信";
              } else {
                return text;
              }
            } else {
              if (text === 1) {
                return "微信";
              } else if (text === 2) {
                return "独家币";
              } else if (text === 3) {
                return "独家钻";
              }
            }
          }
        },
        // {
        //   title: '交易码',
        //   align:"center",
        //   dataIndex: 'payCode'
        //  },
        {
          title: "订单编号",
          align: "center",
          dataIndex: "code"
        },
        // {
        //   title: '微信Code',
        //   align:"center",
        //   dataIndex: 'wxCode'
        //  },
        {
          title: "打赏用户（主播打赏以及进入付费直播时有该用户）/分销的用户",
          align: "center",
          dataIndex: "fromUserId"
        },
        {
          title: "充值独家币实际支付金额",
          align: "center",
          dataIndex: "trueMoney"
        }
      ],
      url: {
        selectlist: "/system/appusermoneydetail/findTypeSelect",
        list: "/system/appusermoneydetail/pageList",
        delete: "/user/appUserMoneyDetail/delete"
      }
    };
  },
  created() {
    getAction(this.url.selectlist)
      .then(res => {
        if (res.success) {
          this.typeList = res.result;
        } else {
          this.$message.warning(res.message);
        }
      })
      .catch(err => {
        this.$message.warning(err.message);
      });
  },
  methods: {

    getQueryParams() {
      //获取查询条件
      let sqp = {};
      if (this.superQueryParams) {
        sqp["superQueryParams"] = encodeURI(this.superQueryParams);
      }
      var param = Object.assign(
        sqp,
        this.queryParam,
        this.isorter,
        this.filters
      );
      param.field = this.getQueryField();
      param.pageNo = this.ipagination.current;
      param.pageSize = this.ipagination.pageSize;
      param.detailType = param.detailType ? param.detailType : "";
      param.search = param.search ? param.search : "";
      param.type = param.type ? param.type : "";
      param.payType = param.payType ? param.payType : "";
      return filterObj(param);
    }
  }
};
