import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import { getAction, postAction } from "@/api/manage";

export default {
  name: "VstatrsTong",
  mixins: [JeecgListMixin],
  components: {
    ProductNewBigImg
  },
  data() {
    return {
      description: "V值统计列表",
    //   queryParam: {
    //       marker:this.$route.query.marker   
    //   },
    //   param: {
    //        marker:this.$route.query.marker       
    //   },
      // 表头
      columns: [
        {
          title: "手机号",
          align: "center",
          dataIndex: "phone"
        },
        {
          title: "V云号",
          align: "center",
          dataIndex: "code"
        },
        {
          title: "昵称",
          align: "center",
          dataIndex: "nickName"
        },
        {
          title: "头像",
          align: "center",
          dataIndex: "header",
          scopedSlots: { customRender: "header" }
        },
        {
          title: "角色",
          align: "center",
          dataIndex: "role",
          customRender: function(text) {
            if (text == "0") {
              return "普通会员";
            } else if (text == "1") {
              return "VIP会员";
            } else if (text == "2") {
              return "大V会员";
            }
          }
        },
        {
          title: "创客达人",
          align: "center",
          dataIndex: "isCreator",
          customRender: function(text) {
            if (text == 1) {
              return "是";
            } else {
              return "否";
            }
          }
        },
        {
          title: "V云大使",
          align: "center",
          dataIndex: "ambassador",
          customRender: function(text) {
            if (text == 1) {
              return "是";
            } else {
              return "否";
            }
          }
        },
        {
          title: "大使星级",
          align: "center",
          dataIndex: "ambassadorLevel"
        },
        {
          title: "大V值",
          align: "center",
          dataIndex: "credits"
        },
        {
          title: "用户标识",
          align: "center",
          dataIndex: "marker",
          customRender: function(text) {
            if (text == 0) {
              return "测试";
            } else if (text == 1) {
              return "正式";
            } else if (text == 2) {
              return "官方";
            }
          }
        }
      ],
      url: {
        list: "/system/appuserinfo/list"
      }
    };
  },
  //   beforeRouteEnter (to, from, next) {
  // 　　console.log(from);
  // 　　next();
  // },
  created() {
    console.log(this.$route.query.marker);
    console.log(this.marker);
  },
  methods: {}
};
