import { filterObj } from "@/utils/util";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";

export default {
  name: "UserUnderList",
  mixins: [JeecgListMixin],

  data() {
    return {
      type: "ambassadorList1",
      inviteId: null,
      visible: false,
      confirmLoading: false,
      teamId: null,
      loading: false,
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      },
      // 表头
      columns: [
        {
          title: "用户v云id",
          align: "center",
          dataIndex: "code"
        },
        {
          title: "用户昵称",
          align: "center",
          dataIndex: "nickName"
        },

        {
          title: "手机号",
          align: "center",
          dataIndex: "phone"
        },
        {
          title: "会员充值总费用",
          align: "center",
          dataIndex: "vipTotalMoney"
        },
        {
          title: "服务商充值总费用",
          align: "center",
          dataIndex: "serviceTotalMoney"
        }
      ],
      url: {
        list: "/system/appuserinfo/umbrella-user-list"
      }
    };
  },
  created() {},
  methods: {
    getQueryParams() {
      //获取查询条件
      let sqp = {};
      if (this.superQueryParams) {
        sqp["superQueryParams"] = encodeURI(this.superQueryParams);
      }
      var param = Object.assign(
        sqp,
        this.queryParam,
        this.isorter,
        this.filters
      );
      param.field = this.getQueryField();
      param.pageNo = this.ipagination.current;
      param.pageSize = this.ipagination.pageSize;
      param.inviteId = this.inviteId;
      param.phone = param.phone ? param.phone : "";
      param.code = param.code ? param.code : "";
      return filterObj(param);
    },
    open(record) {
      console.log(record)
      this.visible = true;
      this.queryParam = {};
      this.inviteId = record.inviteId;
      this.ipagination.current = 1;
      this.loadData();
    },
    handleCancel() {
      this.$emit("close");
      this.visible = false;
      this.dataSource = [];
    }
  }
};
