// import flv from "flv.js"
import videojs from "video.js";
import "videojs-flash";
import { postAction, putAction, getAction } from "@/api/manage";
export default {
  name: "screenMonitoring",

  data() {
    return {
      jie: "/system/live/room/list",
      list: [],
      listData:[],
      playlist:[]
    };
  },
  beforeDestroy() {
    //  this.myPlayer.dispose()
  for(let i=0;i<this.playlist.length;i++){
     this.playlist[i].dispose(); //（第四步）dispose()是官方的销毁函数
    }
    
  },
  // created() {
  //   console.log("初始化")
  //   this.getViewList();
  // },
  async mounted() {
    await this.getViewList();
    await this.initVideo();
  },
  methods: {
    getViewList() {
      return getAction(this.jie).then((res) => {
          if (res.success) {
            console.log(res)
            this.list = res.result.records;
            console.log(this.list)
            for(let i of this.list){
              if(i.state === 1){
              let arrStr = {
                coverImg:i.coverImg,
                streamAddrUrl:i.streamAddrUrl,
                id:i.id,
                nickName:i.nickName,
                code:i.code,
                merchant:i.merchant
              }
              this.listData.push(arrStr)
              console.log(this.listData)
              if(this.listData.length === 0){
                alert("当前没有人在直播哦~")
              }else{
                return;
              }
              }
            }
          } else {
            this.$message.success(res.message);
          }
        })
        .catch((err) => {
          this.$message.success(err.message);
        });
    },

    initVideo() {
      this.listData.map((item, i) => {
        var myPlayer = this.$video("myVideo" + item.id, {
          controls: true,
          preload: "auto",
          width: "400px",
          height: "400px",
          //封面图
          poster: item.coverImg,
        });
        this.playlist.push(myPlayer)
        console.log(this.playlist)
      });
    },
    
  },
};
