import JEllipsis from "@/components/jeecg/JEllipsis";
export default {
  name: "LiveBan",
  mixins: [JeecgListMixin],
  components: {
    ProductNewBigImg,
    JEllipsis
  },

  data() {
    return {
      description: "直播间封禁",
      dataSource: [],
      columns: [
        {
          title: "用户名称",
          align: "center",
          dataIndex: "nickName"
        },
        {
          title: "房间号",
          align: "center",
          dataIndex: "code"
        },
        {
          title: "封禁原因",
          align: "center",
          dataIndex: "reason",
          scopedSlots: { customRender: "templateTitle" }
        },
        {
          title: "封禁级别",
          align: "center",
          dataIndex: "level"
        },
        {
          title: "创建时间",
          align: "center",
          dataIndex: "createTime",
          scopedSlots: { customRender: "createTime" }
        }
        // {
        //   title: "更新时间",
        //   align: "center",
        //   dataIndex: "modifyTime"
        // }
        // {
        //   title: "操作",
        //   dataIndex: "action",
