import {JeecgListMixin} from '@/mixins/JeecgListMixin'
import JEllipsis from "@/components/jeecg/JEllipsis";

export default {
  name: "AllChatMsgRecord",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis
  },
  data() {
    return {
      description: '消息模板管理页面',
      queryParam: {
        groupName: '',
        type: null
      },
      param: {
        groupName: '',
        type: null
      },
      // 表头
      columns: [
        {
          title: '群名称',
          align: "center",
          dataIndex: 'name'
        },
        {
          title: '发言人',
          align: "center",
          dataIndex: 'nickName'
        },
        {
          title: '消息类型',
          align: "center",
          dataIndex: 'meaasgeType',
          customRender: function(text) {
            if (text === 0) {
              return '文本'
            } else if (text === 2) {
              return '音频'
            } else if (text === 3) {
              return '图片'
            } else if (text === 4) {
              return '文件'
            } else if (text === 5) {
              return '视频'
            }
          }
        },
        {
          title: '消息内容',
          align: "center",
          dataIndex: 'meaasgeContent',
          scopedSlots: {customRender: 'templateContent'},
        },
        {
          title: '发言时间',
          align: "center",
          dataIndex: 'msgtime'
        },
      ],
      url: {
        list: "/system/groupHisMessage/list",
        exportXlsUrl: "message/sysMessageTemplate/exportXls",
        importExcelUrl: "message/sysMessageTemplate/importExcel",
      },
    }
  },
  computed: {},
  methods: {
    handleTest(record){
      this.$refs.testModal.open(record);
      this.$refs.testModal.title = "发送测试";
    }

  }
}
