import { getAction, postAction } from "@/api/manage";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import ChatGroupMemberModal from "./modules/ChatGroupMemberModal";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import { filterObj } from "@/utils/util";
import JEllipsis from "@/components/jeecg/JEllipsis";

export default {
  name: "ChartGroupList",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis,
    ProductNewBigImg,
    ChatGroupMemberModal
  },
  data() {
    return {
      description: "群组管理页面",
      // 表头
      columns: [
        {
          title: "群组名称",
          align: "center",
          dataIndex: "name"
        },
        {
          title: "群头像",
          align: "center",
          dataIndex: "icon",
          scopedSlots: { customRender: "icon" }
        },
        {
          title: "群组描述",
          align: "center",
          dataIndex: "descr",
          scopedSlots: { customRender: "templateContent" }
        },
        {
          title: "群组类型",
          align: "center",
          dataIndex: "publicGroup",
          customRender: function(text) {
            if (text === 0) {
              return "私有群";
            } else if (text === 1) {
              return "公开群";
            }
          }
        },
        {
          title: "加入群组需要审批",
          align: "center",
          dataIndex: "membersonly",
          customRender: function(text) {
            if (text === 0) {
              return "不需要";
            } else if (text === 1) {
              return "需要";
            }
          }
        },
        {
          title: "允许群成员邀请别人加入",
          align: "center",
          dataIndex: "allowinvites",
          customRender: function(text) {
            if (text === 0) {
              return "不允许";
            } else if (text === 1) {
              return "允许";
            }
          }
        },
        {
          title: "群最大成员数",
          align: "center",
          dataIndex: "maxusers"
        },
        {
          title: "现有成员总数",
          align: "center",
          dataIndex: "affiliationsCount"
        },
        {
          title: "邀请加群，被邀请人需要确认",
          align: "center",
          dataIndex: "inviteNeedConfirm",
          customRender: function(text) {
            if (text === 0) {
              return "不需要";
            } else if (text === 1) {
              return "需要";
            }
          }
        },
        {
          title: "管理员id",
          align: "center",
          dataIndex: "ownerId"
        },
        {
          title: "二维码",
          align: "center",
          dataIndex: "qrCode",
          scopedSlots: { customRender: "yqcode" }
        },
        {
          title: "全体禁言",
          align: "center",
          dataIndex: "bannedPost",
          customRender: function(text) {
            if (text === 0) {
              return "否";
            } else if (text === 1) {
              return "是";
            }
          }
        },
        {
          title: "阅后即焚",
          align: "center",
          dataIndex: "burnRead",
          customRender: function(text) {
            if (text === 0) {
              return "否";
            } else if (text === 1) {
              return "是";
            }
          }
        },
        {
          title: "公告",
          align: "center",
          dataIndex: "notice",
          scopedSlots: { customRender: "templateContent" }
        },
        {
          title: "操作",
          dataIndex: "state",
          align: "center",
          // width: 200,
          scopedSlots: { customRender: "action" }
        }
      ],
      url: {
        list: "/system/chatgroup/findPageList",
        freeze: "/system/chatgroup/forbidden/json",
        thaw: "/system/chatgroup/thaw/json"
      }
    };
  },

  methods: {
    getQueryParams() {
      //获取查询条件
      let sqp = {};
      if (this.superQueryParams) {
        sqp["superQueryParams"] = encodeURI(this.superQueryParams);
      }
      var param = Object.assign(
        sqp,
        this.queryParam,
        this.isorter,
        this.filters
      );
      param.field = this.getQueryField();
      param.pageNo = this.ipagination.current;
      param.pageSize = this.ipagination.pageSize;
      param.groupName = param.groupName ? param.groupName : "";

      param.groupType =
        param.groupType === 0 || param.groupType === 1 ? param.groupType : "";

      return filterObj(param);
    },

    //  打开成员列表页
    handleMembers(record) {
      this.$refs.modalForm.open(record);
    },
    handleFreeze(id) {
      let arr = `?groupId=${id}`;
      let that = this;
      postAction(this.url.freeze + arr).then(res => {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
          that.loadData();
        }
      });
    },
    handleThaw(id) {
      let arr1 = `?groupId=${id}`;
      let that = this;
      postAction(this.url.thaw + arr1).then(res => {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
          that.loadData();
        }
      });
    }
  }
};
