import { postAction } from "@/api/manage";
import OfficialListModal1 from "./modules/OfficialListModal1";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
export default {
  name: "RevenueReward",
  mixins: [JeecgListMixin],
  components: {
    OfficialListModal1
  },
  data() {
    return {
      description: "收益奖励列表",
      type: "ambassadorList1",
      isAll: "",
      columns: [
        {
          title: "被邀请人信息",
          align: "center",
          dataIndex: "nickName",
          scopedSlots: { customRender: "invit" }
        },

        // {
        //   title: "被邀请人手机号",
        //   align: "center",
        //   dataIndex: "phone"
        // },
        // {
        //   title: "被邀请人v云号",
        //   align: "center",
        //   dataIndex: "code"
        // },
        {
          title: "被邀请人角色",
          align: "center",
          dataIndex: "userRoleName"
        },
        {
          title: "充值总费用",
          align: "center",
          dataIndex: "totalMoney"
        },

        {
          title: "被邀请人分润比例",
          align: "center",
          dataIndex: "rate1",
          scopedSlots: { customRender: "fenRate1" }
        },
        {
          title: "被邀请人分润金额",
          align: "center",
          dataIndex: "shareProfitMoney1",
          scopedSlots: { customRender: "share" }
        },
        // {
        //   title: "邀请人手机号",
        //   align: "center",
        //   dataIndex: "invitePhone"
        // },
        // {
        //   title: "邀请人账号",
        //   align: "center",
        //   dataIndex: "inviteCode"
        // },
        {
          title: "邀请人信息",
          align: "center",
          dataIndex: "inviteNickName",
          scopedSlots: { customRender: "invit1" }
        },
        {
          title: "邀请人角色",
          align: "center",
          dataIndex: "inviteUserRoleName"
        },

        {
          title: "邀请人分润比例",
          align: "center",
          dataIndex: "rate",
          scopedSlots: { customRender: "fenRate" }
        },

        {
          title: "邀请人分润金额",
          align: "center",
          dataIndex: "shareProfitMoney",
          scopedSlots: { customRender: "share" }
        },
        {
          title: "操作",
          align: "center",
          dataIndex: "rids",
          width: 100,
          scopedSlots: { customRender: "action" }
        }
      ],
      url: {
        list: "/system/appuserinfo/earnings-reward-list",
        share: "/system/appuserinfo/earnings-reward-profit"
      }
    };
  },
  methods: {
    // 单个分润
    shareRate: function(record) {
      var that = this;
      let arr = `?rids=${record.rids}`;

      postAction(that.url.share + arr).then(res => {
        if (res.success) {
          that.$message.success(res.message);
          console.log(res);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    }
  }
};
