import ProviderModals from "./modules/ProviderModals";
import { postAction, deleteAction, putAction } from "@/api/manage";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import JEllipsis from "@/components/jeecg/JEllipsis";

export default {
  name: "ProviderList",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis,
    ProviderModals
  },
  data() {
    return {
      description: "服务商分销比例列表",
      fenxiao: true,
      vipsorter: {
        order: "desc"
      },
      // 表头
      columns: [
        {
          title: "角色",
          align: "center",
          dataIndex: "role",
          customRender: function(text) {
            if (text == "1") {
              return "VIP";
            } else if (text == "2") {
              return "创客达人";
            } else if (text == "3") {
              return "大使服务商";
            }
          }
        },
        {
          title: "星级",
          align: "center",
          dataIndex: "level"
        },
        {
          title: "VIP分销",
          align: "center",
          dataIndex: "vipRatio",
          scopedSlots: { customRender: "rebate" }
        },
        {
          title: "创客达人分销",
          align: "center",
          dataIndex: "creatorRatio",
          scopedSlots: { customRender: "rebate1" }
        },
        {
          title: "大使服务商分销",
          align: "center",
          dataIndex: "ambassadorRatio",
          scopedSlots: { customRender: "rebate2" }
        },

        {
          title: "升级数量",
          align: "center",
          dataIndex: "upgradeNum"
        },

        {
          title: "操作",
          dataIndex: "action",
          align: "center",
          scopedSlots: { customRender: "action" }
        }
      ],
      url: {
        list: "/system/service/distribution/list",
        delete: "/system/service/distribution/delete"
      }
    };
  },
  methods: {
    handleDelete: function(record) {
      console.log(record);
      var id = record.id;
      if (!this.url.delete) {
        this.$message.error("请设置url.delete属性!");
        return;
      }
      var that = this;
      deleteAction(that.url.delete, { id: id }).then(res => {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    }
  }
};
