import { getAction } from "@/api/manage";

export default {
  name: "BigTeamAddModal",
  data () {
    return {
      visible: false,
      confirmLoading: false,
      teamId: null,
      loading:false,
      dataSource:[],
      form: this.$form.createForm(this),
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
      // 表头
      columns: [
        {
          title: '昵称',
          align:"center",
          dataIndex: 'nickName'
        },
        {
          title: '手机号',
          align:"center",
          dataIndex: 'phone'
        },
        {
          title: 'V云号',
          align:"center",
          dataIndex: 'code'
        },
        {
          title: '操作',
          dataIndex: 'action',
          align:"center",
          scopedSlots: { customRender: 'action' },
        }
      ],
      url: {
        list: '/system/appuserinfo/findUserListByCode',
        add: '/system/appuserinfo/updateTeamByUserId'
      }
    }
  },
  methods: {
    open (record) {
      this.form.resetFields()
      this.visible = true;
      this.teamId = record.id
    },
    onSearch(value) {
      if(!this.url.list){
        this.$message.error("请设置url.list属性!")
        return
      }
      this.form.validateFields((err) => {
        if (!err) {
          var params = { code: value };//查询条件
          this.loading = true;
          getAction(this.url.list, params).then((res) => {
            if (res.success) {
              this.dataSource = res.result;
            }
            if(res.code===510){
              this.$message.warning(res.message)
            }
            this.loading = false;
          })
        }
      })

    },
    handleCancel () {
      this.$emit('close');
      this.visible = false;
      this.dataSource = [];
    },
    //  添加成员
    lookAdd (userId) {
      const param = {
        teamId: this.teamId,
        userId: userId
      }
      const that = this;
      that.confirmLoading = true;
      getAction(this.url.add, param).then(res => {
        if(res.success){
          that.$message.success(res.message);
          this.$emit('ok')
        }else{
          that.$message.warning(res.message);
        }
      }).finally(() => {
        that.confirmLoading = false
        this.handleCancel()
      })
    }
  }
}
