import { postAction, deleteAction, putAction } from "@/api/manage";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import { filterObj } from "@/utils/util";
import aduitModal from "./modules/aduitModal";
import JEllipsis from "@/components/jeecg/JEllipsis";
import InformationModals from "./modules/InformationModals";
export default {
  name: "ExpressCompany",
  mixins: [JeecgListMixin],
  components: {
    InformationModals,
    JEllipsis,
    ProductNewBigImg,
    aduitModal
  },

  data() {
    return {
      description: "商品信息列表",
      header: [],
      columns: [
        {
          title: "商品所属人",
          align: "center",
          dataIndex: "nickName"
        },
        {
          title: "商品名称",
          align: "center",
          dataIndex: "name",
          scopedSlots: { customRender: "templateTitle" },
          width: 250
        },
        {
          title: "商品价格",
          align: "center",
          dataIndex: "money"
        },
        {
          title: "商品图片",
          align: "center",
          dataIndex: "imageList",
          scopedSlots: { customRender: "avatar" },
          width: 200
        },
        {
          title: "商品来源",
          align: "center",
          dataIndex: "source",
          customRender: function(text) {
            if (text == 1) {
              return "商城";
            } else {
              return "商家";
            }
          }
        },
        {
          title: "商品编号",
          align: "center",
          dataIndex: "goodsNo"
        },
        {
          title: "库存",
          align: "center",
          dataIndex: "stock"
        },
        {
          title: "卖出商品数量",
          align: "center",
          dataIndex: "sellGoodsNum"
        },
        {
          title: "一级返佣",
          align: "center",
          dataIndex: "firstRebate",
          scopedSlots: { customRender: "rebate" }
        },
        {
          title: "二级返佣",
          align: "center",
          dataIndex: "secondRebate",
          scopedSlots: { customRender: "rebateL" }
        },
        {
          title: "商品状态",
          align: "center",
          dataIndex: "shelvesState",
          customRender: function(text) {
            if (text == 0) {
              return "下架";
            } else if (text == 1) {
              return "上架";
            }
          }
        },
        {
          title: "审核原因",
          align: "center",
          dataIndex: "reason",
          scopedSlots: { customRender: "templateTitle" },
          width: 150
        },
        {
          title: "审核状态",
          align: "center",
          dataIndex: "state",
          customRender: function(text) {
            if (text == 0) {
              return "待审核";
            } else if (text == 1) {
              return "已审核";
            } else if (text == 2) {
              return "审核失败";
            }
          }
        },
        {
          title: "操作",
          dataIndex: "action",
          align: "center",
          scopedSlots: { customRender: "action" }
        }
      ],
      url: {
        list: "/system/mall/goods/list",
        delete: "/system/mall/goods/delete",
        upAllow: "/system/mall/goods/upAndLowShelves"
      }
    };
  },

  methods: {
    handleMembersList(record) {
      this.$refs.modalForm.open(record);
    },
    handleDelete: function(record) {
      var id = record.id;
      if (!this.url.delete) {
        this.$message.error("请设置url.delete属性!");
        return;
      }
      var that = this;
      deleteAction(that.url.delete, { cId: id }).then(res => {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    },
    //  图片点击放大
    bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    },
    handleUp: function(record) {
      console.log(record);
      let that = this;
      var roomId = record.id;
      var state = record.shelvesState;
      if (state === 0) {
        var en = 1;
        var arr = `?shelvesState=${en}&goodId=${roomId}`;
      } else if (state === 1) {
        var en = 0;
        var arr = `?shelvesState=${en}&goodId=${roomId}`;
      }
      putAction(this.url.upAllow + arr)
        .then(res => {
          if (res.success) {
            that.$message.success(res.message);
            that.$emit("ok");
            that.loadData();
          } else {
            that.$message.warning(res.message);
          }
        })
        .finally(() => {
          that.confirmLoading = false;
        });
    },
    handleAduit: function(record) {
      console.log(record);
      this.$refs.changeModel.open(record);
      //   var arr = `?goodId=${record.id}`;
      //   var that = this;
      //   putAction(that.url.audit + arr).then(res => {
      //     if (res.success) {
      //       that.$message.success(res.message);
      //       that.loadData();
      //     } else {
      //       that.$message.warning(res.message);
      //     }
      //   });
    }
  }
};
