import { httpAction } from "@/api/manage";
import pick from "lodash.pick";

export default {
  name: "ProviderModals",
  data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      },

      confirmLoading: false,
      autoSubmit: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/system/service/distribution/add",
        edit: "/system/service/distribution/update"
      }
    };
  },
  created() {},
  methods: {
    makeNumber(rule, value, callback) {
      var re = /^([1-9]\d*|[0]{1,1})$/;
      if (!re.test(value)) {
        callback("请输入正整数");
      } else {
        callback();
      }
    },
    add() {
      this.edit({});
    },
    edit(record) {
       	if(record && record.id && record.id !== '' && record.id !== undefined) {
			this.autoSubmit = true;
			console.log(this.autoSubmit)
		} else {
			this.autoSubmit = false;
			console.log(this.autoSubmit);
		}
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(() => {
        // this.$nextTick：等你页面刷新完之后，再执行回调函数中的方法
        this.form.setFieldsValue(
          pick(
            this.model,
            "role",
            "level",
            "vipRatio",
            "creatorRatio",
            "ambassadorRatio",
            "upgradeNum"
          )
        );
        // pick：返回一个只有列入挑选key属性的对象。其中，参数object为JSON格式的对象，*keys表示多个需要挑选出来的key属性。
      });
    },
    close() {
      this.$emit("close");
      this.visible = false;
    },
    handleOk() {
      const that = this;
      // 触发表单验证
      this.form.validateFields((err, values) => {
        if (!err) {
          that.confirmLoading = true;
          let httpurl = "";
          let method = "";
          if (!this.model.id) {
            httpurl += this.url.add;
            method = "post";
          } else {
            httpurl += this.url.edit;
            method = "put";
          }
          let formData = Object.assign(this.model, values);
          //时间格式化
          console.log(formData);
          httpAction(httpurl, formData, method)
            .then(res => {
              if (res.success) {
                that.$message.success(res.message);
                that.$emit("ok");
              } else {
                that.$message.warning(res.message);
              }
            })
            .finally(() => {
              that.confirmLoading = false;
              that.close();
            });
        }
      });
    },
    handleCancel() {
      this.close();
    }
  }
};
