import { getAction } from "@/api/manage";
import WithdrawModal from "./modules/WithdrawModal";
import WithdrawAuditModal from "./modules/WithdrawAuditModal";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import { formatDate } from "@/utils/util";
import JEllipsis from "@/components/jeecg/JEllipsis";

export default {
  name: "WithdrawList",
  mixins: [JeecgListMixin],
  components: {
    JEllipsis,
    WithdrawModal,
    WithdrawAuditModal,
  },
  data() {
    return {
      description: "提现记录表管理页面",
      queryParam: {
        status: "",
      },
      param: {
        status: "",
      },
      // 表头
      columns: [
        {
          title: "用户昵称",
          align: "center",
          dataIndex: "nickName",
        },
        {
          title: "提现金额",
          align: "center",
          dataIndex: "money",
          customRender: function (text) {
            return text + "元";
          },
        },
        {
          title: "提现的独家钻",
          align: "center",
          dataIndex: "diamond",
          customRender: function (text) {
            return text + "个";
          },
        },
        {
          title: "房间号",
          align: "center",
          dataIndex: "code",
        },
        {
          title: "提现时间",
          align: "center",
          dataIndex: "createTime",
          customRender: function (text) {
            return formatDate(text, "yyyy-MM-dd");
          },
        },
        {
          title: "状态",
          align: "center",
          dataIndex: "status",
          customRender: function (text) {
            if (text === 1) {
              return "申请中";
            } else if (text === 2) {
              return "申请成功";
            } else if (text === 3) {
              return "申请失败";
            }
          },
        },
        {
          title: "资金流向",
          align: "center",
          dataIndex: "type",
          customRender: function (text) {
            if (text === 1) {
              return "微信";
            } else if (text === 2) {
              return "银行卡";
            } else {
              return " ";
            }
          },
        },
        {
          title: "申请提现理由",
          align: "center",
          dataIndex: "reason",
          scopedSlots: { customRender: "templateAddress" },
        },
        {
          title: "失败原因",
          align: "center",
          dataIndex: "failReason",
          scopedSlots: { customRender: "templateAddress" },
        },
        {
          title: "申请通过时间",
          align: "center",
          dataIndex: "successTime",
          customRender: function (text) {
            if (text) {
              return formatDate(text, "yyyy-MM-dd");
            } else {
              return " ";
            }
          },
        },
        {
          title: "申请失败时间",
          align: "center",
          dataIndex: "failTime",
          customRender: function (text) {
            if (text) {
              return formatDate(text, "yyyy-MM-dd");
            } else {
              return " ";
            }
          },
        },
        {
          title: "银行卡信息",
          align: "center",
          dataIndex: "userBankId",
        },
        {
          title: "阿里账号",
          align: "center",
          dataIndex: "aliId",
        },
        {
          title: "阿里名字",
          align: "center",
          dataIndex: "aliName",
        },
        {
          title: "操作",
          dataIndex: "action",
          align: "center",
          scopedSlots: { customRender: "action" },
        },
      ],
      url: {
        list: "/system/withdraw/list",
        auditTrue: "/system/withdraw/auditTrue",
      },
    };
  },
  methods: {
    handleOk(id) {
      getAction(this.url.auditTrue, { id: id })
        .then((res) => {
          if (res.success) {
            this.$message.success(res.message);
            this.loadData();
          } else {
            this.$message.success(res.message);
          }
        })
        .catch((err) => {
          this.$message.success(err.message);
        });
    },
    handleRefused(id) {
      this.$refs.modalAudit.open(id);
    },
       searchReset() {
      this.queryParam =  {}
      this.loadData(1);
    },
  },
};
