import { putAction } from "@/api/manage";

export default {
  name: "aduitModal",
  data() {
    return {
      title: "审核状态",
      visible: false,
      reason: "",
      typevulue: 18,
      state: 1,
      confirmLoading: false,
      url: {
        audit: "/system/mall/goods/audit"
      }
    };
  },
  watch: {
    reason() {
      if (this.reason.length > 20) {
        alert("审核原因最多不能超过20个字符");
        this.reason=String(this.reason).slice(0,20)
        return;
      } else if(this.reason.length == 0){
        this.reason == ""
      }
    }
  },
  methods: {
    open(record) {
      this.visible = true;
      this.goodId = record.id;
      this.state = 1;
      this.reason == "";
    },

    close() {
      this.$emit("close");
      this.visible = false;
      this.state = null;
      this.reason = '';
    },
    // 点击确定后的回调函数
    handleOk() {
      const that = this;
      let arr = "";
      that.confirmLoading = true;
      arr = `?goodId=${this.goodId}&state=${this.state}&reason=${this.reason}`;
      console.log(arr);

      putAction(this.url.audit + arr)
        .then(res => {
          if (res.success) {
            that.$message.success(res.message);
            that.$emit("ok");
          } else {
            that.$message.warning(res.message);
          }
        })
        .finally(() => {
          that.confirmLoading = false;
          that.close();
        });
    },
    handleCancel() {
      this.close();
    }
  }
};
