import Vue from 'vue'
import { ACCESS_TOKEN } from "@/store/mutation-types"
import pick from 'lodash.pick'
import {JeecgListMixin} from '@/mixins/JeecgListMixin'
import { getAction, httpAction } from "@/api/manage";
import JCodeEditor from "@/components/jeecg/JCodeEditor";
import JEditor from "@/components/jeecg/JEditor";
import * as qiniu from 'qiniu-js'

export default {
  name: "WithdrawSettingList",
  mixins: [JeecgListMixin],
  components: {
    JCodeEditor,
    JEditor
  },
  data() {
    return {
      form: this.$form.createForm(this),
      type: null,
      model: {},
      value: '',
      canimg: '',
      percent: '',
      loading: false,
      headers: {},
      labelCol: {
        xs: {span: 24},
        sm: {span: 4}
      },
      wrapperCol: {
        xs: {span: 24},
        sm: {span: 19}
      },
      columns: [
        {
          title: '描述',
          align:"center",
          dataIndex: 'descr'
        },
      ],
      baseUrl: '/system/sysparam/',
      url: {
        list: '/system/sysparam/listForWithdraw',
        edit: '/system/sysparam/edit',
        fileUpload: window._CONFIG['domianQiNiu'],
        qiniuToken: '/qiniuUpToken'
      }
    }
  },
  computed:{
    uploadAction:function () {
      return this.url.fileUpload;
    }
  },
  created () {
    const token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {"X-Access-Token":token};
  },
  methods: {
    loadData(arg) {
      if(!this.url.list){
        this.$message.error("请设置url.list属性!")
        return
      }
      //加载数据 若传入参数1则加载第一页的内容
      if (arg === 1) {
        this.ipagination.current = 1;
      }
      this.loading = true;
      getAction(this.url.list).then((res) => {
        if (res.success) {
          this.dataSource = res.result;
        }
        if(res.code===510){
          this.$message.warning(res.message)
        }
        this.loading = false;
      })
    },
    clickThenCheck (record) {
      return {
        on: {
          click: () => {
            this.model = Object.assign({}, record)
            this.setValuesToForm(record)
          }
        }
      }
    },
    setValuesToForm (record) {
      // this.$nextTick(() => {
        this.form.setFieldsValue(pick(record, 'categoryId', 'chooseFlag', 'code', 'descr', 'id', 'type', 'value'))
      // });
      this.type = record.type;
        this.value = record.value;
      this.canimg = record.value;
    },
    //重置
    emptyCurrForm () {
      this.form.resetFields()
      this.value = '';
    },
  //  提交
    submitCurrForm () {
      this.form.validateFields((err, values) => {
        if (!err) {
          if (!this.url.edit) {
            return
          }
          let formData = Object.assign(this.model, values)
          formData.value = this.value;
          console.log(formData)
          httpAction(this.url.edit, formData, 'put').then(res => {
            if(res.success){
              this.$message.success(res.message);
              this.loadData()
            }else{
              this.$message.warning(res.message);
            }
          })
        }
      })
    },
    // 自定义上传
    customRequest (data) {
      const suffix = "." + data.file.name.replace(/^.+\./, '')
      //请求token
      httpAction(this.url.qiniuToken, {}, 'post').then((res) => {
        if (res) {
          let token = res.data.token
          const file = data.file
          const key = new Date().getTime() + suffix;
          let domain = this.url.fileUpload
          let config = {
            useCdnDomain: true,
            region: null
          }
          let putExtra = {
            fname: '',
            params: {},
            mimeType: null
          }
          let observable = qiniu.upload(file, key, token, putExtra, config);
          observable.subscribe({
            next: (res) => {
              this.loading = true;
              this.percent = res.total.percent.toFixed(2)
              this.value = '';
            },
            error: () => {
              this.$message.error('上传失败')
              this.loading = false;
              this.value = this.canimg
            },
            complete: (response) => {
              this.value = `${domain}/${response.key}`;
              this.loading = false;
            }
          })
        }
      })
    },
  },
  watch: {

  }
}
