import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import InvitaionMoudles from "./modules/InvitaionMoudles";
export default {
  name: "RelationShip",
  mixins: [JeecgListMixin],
  components: {
    InvitaionMoudles
  },
  data() {
    return {
      description: "邀请奖励关系表",
      columns: [
        {
          title: "被邀请用户v云号",
          align: "center",
          dataIndex: "invitedCode"
        },
        {
          title: "被邀请用户名称",
          align: "center",
          dataIndex: "invitedName"
        },
        {
          title: "被邀请用户角色",
          align: "center",
          dataIndex: "invitedRole",
          customRender: function(text) {
            if (text == "1") {
              return "VIP";
            } else if (text == "2") {
              return "创客达人";
            } else if (text == "3") {
              return "大使服务商";
            }
          }
        },
        {
          title: "邀请用户v云号",
          align: "center",
          dataIndex: "code"
        },
        {
          title: "邀请用户名称",
          align: "center",
          dataIndex: "nickName"
        },
        {
          title: "邀请用户角色",
          align: "center",
          dataIndex: "role",
          customRender: function(text) {
            if (text == "1") {
              return "VIP";
            } else if (text == "2") {
              return "创客达人";
            } else if (text == "3") {
              return "大使服务商";
            }
          }
        },
        {
          title: "直推比例",
          align: "center",
          dataIndex: "ratio"
        },
        // {
        //   title: "操作",
        //   dataIndex: "antionSet",
        //   align: "center",
        //   scopedSlots: { customRender: "antionSet" }
        // }
      ],
      url: {
        list: "/system/invite/relation/list"
      }
    };
  },
  methods: {}
};
