import { getAction } from '@/api/manage'
import JeecgOrderDMainModal from '@/views/jeecg/tablist/form/JeecgOrderDMainModal'
import {JeecgListMixin} from '@/mixins/JeecgListMixin'

export default {
  name: "TableDemo",
  mixins: [JeecgListMixin],
  components: {
    JeecgOrderDMainModal
  },
  data() {
    return {
      // 子表表头
      innerColumns: [
        {
          title: '客户名',
          align: "center",
          width: 100,
          dataIndex: 'name',
          key: 'name',
        },
        {
          title: '性别',
          align: "center",
          dataIndex: 'sex',
          customRender: function (text) {
            if (text == 1) {
              return "男";
            } else if (text == 2) {
              return "女";
            } else {
              return text;
            }
          }
        },
        {
          title: '身份证号码',
          align: "center",
          dataIndex: 'idcard',
        },
        {
          title: '电话',
          dataIndex: 'telphone',
          align: "center",
        },

      ],
      innerData: [],
      expandedRowKeys: [],
      id: ' ',
      description: '列表展开子表Demo',
      // 列表表头
      columns: [{
        title: '#',
        dataIndex: '',
        key: 'rowIndex',
        width: 60,
        align: "center",
        customRender: function (t, r, index) {
          return parseInt(index) + 1;
        }
      },
        {
          title: '订单号',
          align: "center",
          dataIndex: 'orderCode'
        },
        {
          title: '订单类型',
          align: "center",
          dataIndex: 'ctype',
          customRender: (text) => {
            let re = "";
            if (text === '1') {
              re = "国内订单";
            } else if (text === '2') {
              re = "国际订单";
            }
            return re;
          }
        },
        {
          title: '订单日期',
          align: "center",
          dataIndex: 'orderDate'
        },
        {
          title: '订单金额',
          align: "center",
          dataIndex: 'orderMoney'
        },
        {
          title: '订单备注',
          align: "center",
          dataIndex: 'content'
        },
        {
          title: '操作',
          dataIndex: 'action',
          align: "center",
          scopedSlots: {customRender: 'action'},
        }],
      // 分页参数
      type: "radio",
      url: {
        list: "/test/order/orderList",
        delete: "/test/order/delete",
        deleteBatch: "/test/order/deleteBatch",
        customerListByMainId: "/test/order/listOrderCustomerByMainId",
      },
    }
  },
  computed: {
    currentId(){
      return this.id;
    }
  },
  methods: {
    handleExpand(expanded, record){
      this.expandedRowKeys=[];
      this.innerData=[];
      if(expanded===true){
        this.loading = true;
        this.expandedRowKeys.push(record.id);
        getAction(this.url.customerListByMainId, {mainId: record.id}).then((res) => {
          if (res.success) {
            this.loading = false;
            this.innerData = res.result;
          }
        });
      }
    },
  }
}
