import { postAction, deleteAction } from "@/api/manage";
import ProductNewBigImg from "../product/modules/ProductNewBigImg";
import { JeecgListMixin } from "@/mixins/JeecgListMixin";
import JEllipsis from "@/components/jeecg/JEllipsis";
import GiftModals from "./modules/GiftModals";
export default {
  name: "LiveGift",
  mixins: [JeecgListMixin],
  components: {
    ProductNewBigImg,
    JEllipsis,
    GiftModals
    // ChartCard
  },

  data() {
    return {
      description: "直播间礼物列表",
      total: "",
      dataSource: [],
      columns: [
        {
          title: "礼物名称",
          align: "center",
          dataIndex: "name"
        },
        {
          title: "礼物图标",
          align: "center",
          dataIndex: "icon",
          scopedSlots: { customRender: "header" }
        },
        {
          title: "V币",
          align: "center",
          dataIndex: "money"
        },
        {
          title: "排序",
          align: "center",
          dataIndex: "sort"
        },
        {
          title: "是否使用",
          align: "center",
          dataIndex: "state",
          customRender: function(text) {
            if (text == 0) {
              return "是";
            } else if (text == 1) {
              return "否";
            }
          }
        },
        {
          title: "操作",
          dataIndex: "action",
          align: "center",
          scopedSlots: { customRender: "action" }
        }
      ],
      url: {
        list: "/system/live/gift/list",
        delete: "/system/live/gift/delete"
      }
    };
  },
  methods: {
    handleDelete: function(record) {
      var id = record.id;
      if (!this.url.delete) {
        this.$message.error("请设置url.delete属性!");
        return;
      }
      var that = this;
      deleteAction(that.url.delete, { id: id }).then(res => {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      });
    },

    //  图片点击放大
    bigImg(text) {
      if (text) {
        this.$refs.bigImg.make(text);
      }
    }
  }
};
