import { httpAction } from '@/api/manage'
import pick from 'lodash.pick'

export default {
  name: "RechangeModal",
  data () {
    return {
      title:"操作",
      visible: false,
      model: {},
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },

      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules:{
        money: {
          rules: [
            { required: true, message: '请输入支付金额' },
            { validator: this.money }
          ]
        },
        trueMoney: {
          rules: [
            { required: true, message: '请输入实际获取独家币' },
            { validator: this.trueMoney }
          ]
        }
      },
      url: {
        add: "/system/rechange/add",
        edit: "/system/rechange/edit",
      },
    }
  },
  created () {
  },
  methods: {
    money (rule, value, callback) {
      if (value <= 0) {
        callback('金额数值需大于0')
      } else {
        callback()
      }
    },
    trueMoney (rule, value, callback) {
      if (value <= 0) {
        callback('独家币金额需大于0')
      } else {
        callback()
      }
    },
    add () {
      this.edit({});
    },
    edit (record) {
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(() => {
        this.form.setFieldsValue(pick(this.model,'money','trueMoney'))
      });

    },
    close () {
      this.$emit('close');
      this.visible = false;
    },
    handleOk () {
      const that = this;
      // 触发表单验证
      this.form.validateFields((err, values) => {
        if (!err) {
          that.confirmLoading = true;
          let httpurl = '';
          let method = '';
          if(!this.model.id){
            httpurl+=this.url.add;
            method = 'post';
          }else{
            httpurl+=this.url.edit;
             method = 'put';
          }
          let formData = Object.assign(this.model, values);

          console.log(formData)
          httpAction(httpurl,formData,method).then((res)=>{
            if(res.success){
              that.$message.success(res.message);
              that.$emit('ok');
            }else{
              that.$message.warning(res.message);
            }
          }).finally(() => {
            that.confirmLoading = false;
            that.close();
          })
        }
      })
    },
    handleCancel () {
      this.close()
    },


  }
}
