import ProductAuditMoadl from "./modules/ProductAuditMoadl";
import ProductCheckAuditModal from "./modules/ProductCheckAuditModal";
import ProductNewBigImg from "./modules/ProductNewBigImg"
import { JeecgListMixin } from '@/mixins/JeecgListMixin'
import { getAction } from "@/api/manage";
import { formatDate } from '@/utils/util';
import JEllipsis from "@/components/jeecg/JEllipsis";

export default {
  name: "ProductAuditList",
  mixins:[JeecgListMixin],
  components: {
    JEllipsis,
    ProductAuditMoadl,
    ProductNewBigImg,
    ProductCheckAuditModal
  },
  data () {
    return {
      description: '课程审核页面',
      // 表头
      columns: [
        {
          title: '课程标题',
          align:"center",
          dataIndex: 'title',
          scopedSlots: { customRender: 'templateTitle' }
        },
        {
          title: '课程简介',
          align:"center",
          dataIndex: 'introduction',
          scopedSlots: { customRender: 'templateContent' }
        },
        {
          title: '作者昵称',
          align:"center",
          dataIndex: 'nickname'
        },
        {
          title: '课程专辑',
          align:"center",
          dataIndex: 'albumname'
         },
        {
          title: '课程分类',
          align:"center",
          dataIndex: 'catename'
        },
        {
          title: '课程封面',
          align:"center",
          dataIndex: 'coverimg',
          scopedSlots: { customRender: 'avatar' }
        },
        {
          title: '课程价格',
          align:"center",
          dataIndex: 'price'
        },
        {
          title: '审核状态',
          align:"center",
          dataIndex: 'auditstate',
          customRender: function (text) {
            if (text === 1) {
              return '审核中'
            } else if (text === 2) {
              return '审核不通过'
            }
          }
        },
        {
          title: '创建时间',
          align:"center",
          dataIndex: 'createtime',
          customRender: function (text) {
            if (text) {
              return formatDate(text, 'yyyy-MM-dd')
            } else {
              return ' '
            }
          }
        },
        {
          title: '操作',
          dataIndex: 'action',
          align:"center",
          scopedSlots: { customRender: 'action' },
        }
      ],
      selectList: [],
      url: {
        list: "/product/productcheck/list",
        selectNewList:"/product/productCategory/queryCategorySelect",
        auditTrue: '/product/productcheck/auditTrue',    //审核通过
      },
    }
  },
  created () {
    getAction(this.url.selectNewList).then((res) => {
      if (res.success) {
        this.selectList = res.result;
      } else {
        this.$message.warning(res.message);
      }
    })
  },
  methods: {
    clickThenCheck (record) {
      return {
        on: {
          click: () => {
            this.$refs.modalForm.edit(record);
            this.$refs.modalForm.title = "详情";
            this.$refs.modalForm.disableSubmit = true;
          }
        }
      }
    },
    //  图片点击放大
    bigImg (text) {
      if(text) {
        this.$refs.bigImg.make(text);
      }
    },
    //  审核通过，请求接口
    handleAuditTrue (id) {
      if(!this.url.auditTrue){
        this.$message.error("请设置审核属性!")
        return
      }
      var that = this;
      getAction(this.url.auditTrue, {id: id}).then((res) => {
        if (res.success) {
          that.$message.success(res.message);
          that.loadData();
        } else {
          that.$message.warning(res.message);
        }
      })
    },
    //  审核拒绝，弹出框=>拒绝理由
    handleShenJu (id) {
      this.$refs.auditModel.open(id);
    }
  }
}
