import { getAction, postAction } from "@/api/manage";

export default {
  name: "ForbiddenRoom",
  data() {
    return {
      level: 1,
      title: "",
      typeList: [],
      id: "",
      reason: "",
      visible: false,
      confirmLoading: false,
      url: {
        ban: "/system/live/room/ban"
      }
    };
  },
    watch: {
    reason() {
      if (this.reason.length > 20) {
        alert("审核原因最多不能超过20个字符");
        this.reason=String(this.reason).slice(0,20)
        return;
      } else if(this.reason.length == 0){
        this.reason == ""
      }
    }
  },
  methods: {
    open(record) {
      this.reason == "";
      this.visible = true;
      this.roomId = record.id;
      this.state = record.state;
      this.code = record.code;
    },
  
    close() {
      this.$emit("close");
      this.visible = false;
      this.id = "";
      this.type = "";
      this.reason == "";
      this.isShareProfit = null;
    },
    // 点击确定后的回调函数
    handleOk() {
      const that = this;
      if (
        (this.level == undefined || this.level == "") &&
        this.level !== 0
      ) {
        this.$message.warning("数值不能为空！");
        return;
      }
      let thisurl = "";
      that.confirmLoading = true;
      if (this.level == 1) {
        var type = "A";
      } else if (this.level == 2) {
        var type = "B";
      } else if (this.level == 3) {
        var type = "C";
      }
      var state = this.state;
      if (state === 1) {
        var en = 2;
        var arr = `?roomId=${this.roomId}&state=${en}&level=${type}&reason=${this.reason}&code=${this.code}`;
      } else if (state === 2) {
        var en = 1;
        var arr = `?roomId=${this.roomId}&state=${en}&level=${type}&reason=${this.reason}&code=${this.code}`;
      }
      thisurl = this.url.ban;

      // console.log(this.url.setVip);
      console.log(arr);
      postAction(thisurl + arr)
        .then(res => {
          if (res.success) {
            that.$message.success(res.message);
            that.$emit("ok");
          } else {
            that.$message.warning(res.message);
          }
        })
        .finally(() => {
          that.confirmLoading = false;
          that.close();
        });
    },
    handleCancel() {
      this.close();
    }
  }
};
