  import VipModal from './modules/VipModal'
  import { JeecgListMixin } from '@/mixins/JeecgListMixin'

  export default {
    name: "VipList",
    mixins:[JeecgListMixin],
    components: {
      VipModal
    },
    data () {
      return {
        description: '会员表管理页面',
        // 表头
        columns: [
          // {
          //   title: '#',
          //   dataIndex: '',
          //   key:'rowIndex',
          //   width:60,
          //   align:"center",
          //   customRender:function (t,r,index) {
          //     return parseInt(index)+1;
          //   }
          //  },
           {
            title: '名称',
            align:"center",
            dataIndex: 'name'
           },
          {
            title: '价格',
            align:"center",
            dataIndex: 'price'
           },
          {
            title: '每月的价格',
            align:"center",
            dataIndex: 'everyPrice'
          },
          {
            title: '天数',
            align:"center",
            dataIndex: 'days'
           },
          {
            title: '原价',
            align:"center",
            dataIndex: 'oldPrice'
           },
          {
            title: '是否推荐',
            align:"center",
            dataIndex: 'isRecommend',
            customRender: function (text) {
              if (text === 0) {
                return '不推荐';
              } else if (text === 1) {
                return '推荐'
              } else if (text === 2) {
                return '特别推荐'
              }
            }
           },
          {
            title: '是否自动充值会员',
            align:"center",
            dataIndex: 'isAuto',
            customRender: function (text) {
              if (text == '0') {
                return '否'
              } else if (text == '1') {
                return '是'
              }
            }
           },
          {
            title: '描述',
            align:"center",
            dataIndex: 'descr'
           },
          {
            title: '操作',
            dataIndex: 'action',
            align:"center",
            scopedSlots: { customRender: 'action' },
          }
        ],
		url: {
          list: "/other/vip/list",
          delete: "/other/vip/delete",
          deleteBatch: "/other/vip/deleteBatch",
          exportXlsUrl: "other/vip/exportXls",
          importExcelUrl: "other/vip/importExcel",
       },
    }
  },
  computed: {
    importExcelUrl: function(){
      return `${window._CONFIG['domianURL']}/${this.url.importExcelUrl}`;
    }
  },
