import { getAction, postAction } from "@/api/manage";

export default {
  name: "AppSetVip",
  data() {
    return {
      title: "修改",
      id: "",
      visible: false,
      typeList: [],
      type: "",
      typevulue: 18,
      isShareProfit: "", //是否分润
      confirmLoading: false,
      defaultIsVip: false,
      url: {
        setVip: "/system/appuserinfo/setVip",
        rechange: "/system/appuserinfo/rechange-money-list?role=1"
      }
    };
  },
  created() {
    getAction(this.url.rechange)
      .then(res => {
        if (res.success) {
          this.typeList = res.result;
        } else {
          this.$message.warning(res.message);
        }
      })
      .catch(err => {
        this.$message.warning(err.message);
      });
  },
  methods: {
    open(record) {
      this.visible = true;
      this.id = record.userid;
      this.isShareProfit = 1;
    },

    close() {
      this.$emit("close");
      this.visible = false;
      this.id = "";
      this.type = "";
      this.isShareProfit = null;
    },
    // 点击确定后的回调函数
    handleOk() {
      const that = this;
      console.log(this.typevulue);
      if (
        (this.typevulue == undefined || this.typevulue == "") &&
        this.typevulue !== 0
      ) {
        this.$message.warning("数值不能为空！");
        return;
      }
      let arr = "";
      let thisurl = "";
      that.confirmLoading = true;
      // this.type === "setVip"
      arr = `?userid=${this.id}&vipId=${this.typevulue}&isShareProfit=${this.isShareProfit}`;
      thisurl = this.url.setVip;

      // console.log(this.url.setVip);
      // console.log(arr)
      postAction(thisurl + arr)
        .then(res => {
          if (res.success) {
            that.$message.success(res.message);
            that.$emit("ok");
            console.log(thisurl + arr);
          } else {
            that.$message.warning(res.message);
          }
        })
        .finally(() => {
          that.confirmLoading = false;
          that.close();
        });
    },
    handleCancel() {
      this.close();
    }
  }
};
